//JavaScript
var summaryStats = datasources.getSummaryStatistics.latestData();
var getDevices = datasources.getDevices.latestData();

//helper function to build out options for a select from an array of objects
function buildOptionsForSelect(arrayOfOptions, selectElementId, includeEmptyOption) {
    var select = document.getElementById(selectElementId);
    if (includeEmptyOption) {
        select.add(document.createElement("option"));
    }
    for (var x = 0; x < arrayOfOptions.length; x++) {
        var option = document.createElement("option");
        option.text = arrayOfOptions[x].name;
        option.value = arrayOfOptions[x].value;
        if (datasources.selectedDevice.name == arrayOfOptions[x].name) {
            select.value = arrayOfOptions[x].value;
        }
        select.add(option);
    }
}


if (this.datasource.success) {
    var locations = this.datasource.results.DATA;
    document.getElementById("locationCount").innerHTML = locations.length;
    var parsedLocations = [];
    for (var x = 0; x < locations.length; x++) {
        var locObj = {
            name: locations[x].label,
            value: locations[x].name
        };
        parsedLocations.push(locObj);
    }
    buildOptionsForSelect(parsedLocations, "locationSelect", true);
    var locationSelect = document.getElementById("locationSelect");
    locationSelect.addEventListener("change", function(event) {
        var locations = CB_PORTAL.portalModel.getDatasourceInstancesWithNameAsKey().getLocations.latestData().results.DATA;
        var selectedLocation = null;
        for (var x = 0; x < locations.length; x++) {
            if (locations[x].name === event.target.value) {
                selectedLocation = locations[x];
                break;
            }
        }
        if (selectedLocation === null) {
            window.showMapLayer(window.layersObj.allLocations);
            //window.mapObj.fitBounds(window.layersObj.allLocations.getBounds());
        } else {
            if (Object.keys(window.layersObj.devicesPerLocation[selectedLocation.name]._layers).length > 0) {
                window.showMapLayer(window.layersObj.devicesPerLocation[selectedLocation.name]);
            }
            window.mapObj.setView([selectedLocation.latitude, selectedLocation.longitude], 13);
        }
    });

    var deviceSelect = document.getElementById("deviceSelect");
    deviceSelect.addEventListener("change", function(event) {
        if (event.target.value === ""){
            datasources.selectedDevice.updateCallback({});
            datasources.selectedDeviceRecentStream.updateCallback([]);

        } else {
            for (var x = 0; x < getDevices.length; x++) {
                if (getDevices[x].name === event.target.value) {
                    datasources.selectedDevice.updateCallback(getDevices[x]);
                    datasources.selectedDeviceRecentStream.updateCallback([{
                        "pressure": getDevices[x].pressure,
                        "rpm": getDevices[x].rpm,
                        "suction": getDevices[x].suction,
                        "voltage": getDevices[x].voltage,
                        "amps": getDevices[x].amps,
                        "timestamp": new Date().toISOString()
                    }]);
                    break;
                }
            }
        }
    });
}

if (summaryStats.success) {
    document.getElementById("deviceCount").innerHTML = summaryStats.results.active_device_count + "/" + summaryStats.results.device_count;
    document.getElementById("userCount").innerHTML = summaryStats.results.user_count;
    document.getElementById("issueCount").innerHTML = summaryStats.results.issue_count;
}
if (getDevices.length > 0) {
    var parsedDevices = [];
    for (var x = 0; x < getDevices.length; x++) {
        var devObj = {
            name: getDevices[x].name,
            value: getDevices[x].name
        };
        parsedDevices.push(devObj);
    }
    buildOptionsForSelect(parsedDevices, "deviceSelect", true);
}

