//JavaScript

//start initialization related things - everything in this section should only be ran on initial load
if (window.mapObj === undefined) {
    L.mapbox.accessToken = 'pk.eyJ1IjoicnJlaW5vbGQiLCJhIjoiY2lwOTllOGQ2MDB4Y2VhbnJnMWtzcW9qaiJ9.1mNTDQ5rwabgUL9onzaLxg';
    window.mapObj = L.mapbox.map('map', 'mapbox.streets').setView([40, -74.50], 9);
}

//first the layers
if (window.layersObj === undefined) {
    window.layersObj = {};
}

// start device per location layer - these layers will be filled with markers in a bit
if (window.layersObj.devicesPerLocation === undefined) {
    window.layersObj.devicesPerLocation = {};
    //loop through each location and create an empty layer for it and save the reference for later
    if (this.datasource.getLocations.success) {
        var locations = this.datasource.getLocations.results.DATA;
        for (var x = 0; x < locations.length; x++) {
            window.layersObj.devicesPerLocation[locations[x].name] = L.mapbox.featureLayer();
        }
    } else {
        console.log('getLocations was not successful, unable to create layers for devices per location');
    }
}
// end device per location layer

// start allLocations layer - won't use markers since these won't ever move
if (window.layersObj.allLocations === undefined) {
    if (this.datasource.getLocations.success) {
        var locations = this.datasource.getLocations.results.DATA;
        var geojson = [];
        for (var x = 0; x < locations.length; x++) {
            var geojsonobj = {
                type: 'Feature',
                geometry: {
                    type: 'Point',
                    coordinates: [locations[x].longitude, locations[x].latitude]
                },
                properties: {
                    "marker-color": "#000000",
                    "marker-size": "medium",
                    "marker-symbol": "l",
                    title: locations[x].label,
                    description: locations[x].address
                }
            };
            geojson.push(geojsonobj);
        }
        window.layersObj.allLocations = L.mapbox.featureLayer().setGeoJSON(geojson);
    }
}
// end allLocations layer

//done with the layers

//now create the markers as needed
if (window.markersObj === undefined) {
    window.markersObj = {};
}

// start byLocation markers obj creation
if (window.markersObj.byLocation === undefined) {
    window.markersObj.byLocation = {};
    if (this.datasource.getLocations.success) {
        var locations = this.datasource.getLocations.results.DATA;
        for (var x = 0; x < locations.length; x++) {
            window.markersObj.byLocation[locations[x].name] = {};
        }
        //now we get all devices, and create a marker for each and add to the correct location
        var devices = this.datasource.getDevices;
        for (var y = 0; y < devices.length; y++) {
            window.markersObj.byLocation[devices[y].location_id][devices[y].name] = L.marker([devices[y].latitude, devices[y].longitude], {
                icon: L.mapbox.marker.icon({
                    'marker-color': "#ffffff",
                    'marker-symbol': 'd'
                })
            }).addTo(window.layersObj.devicesPerLocation[devices[y].location_id]);
        }
        //that is it for now
    }
}

//done creating markers

//add some helper functions for showing/hiding layers
if (window.showMapLayer === undefined) {
    window.showMapLayer = function (layerObj) {
        //hide all other layers - probably a better way to do this in the future.
        //hide allLocations first
        if (window.mapObj.hasLayer(window.layersObj.allLocations)) {
            window.mapObj.removeLayer(window.layersObj.allLocations);
        }
        //no loop through each device by location layer and hide it
        for (var loc in window.layersObj.devicesPerLocation) {
            if (window.layersObj.devicesPerLocation.hasOwnProperty(loc)) {
                if (window.mapObj.hasLayer(window.layersObj.devicesPerLocation[loc])) {
                    window.mapObj.removeLayer(window.layersObj.devicesPerLocation[loc]);
                }
            }
        }
        layerObj.addTo(window.mapObj);
        window.mapObj.fitBounds(layerObj.getBounds());
    }
}

//show allLocations layer by default
if (window.initialMapParserLoaded === undefined && !window.initialMapParserLoaded) {
    window.initialMapParserLoaded = true;
}

// If a location has already been selected, display it.
var locSelect = document.getElementById("locationSelect");
var selectedLocation = !!locSelect ? locSelect.value : "";
if (selectedLocation !== "") {
    window.layersObj.devicesPerLocation[selectedLocation].addTo(window.mapObj);
} else {
    window.showMapLayer(window.layersObj.allLocations);
}


// end initialization

if (window.lastRealtimeAssetPositions === undefined) {
    window.lastRealtimeAssetPositions = this.datasource.realtimeAssetPositions;
} else {
    if (window.lastRealtimeAssetPositions !== this.datasource.realtimeAssetPositions) {
        window.lastRealtimeAssetPositions = this.datasource.realtimeAssetPositions;
        var parsedPosition = JSON.parse(window.lastRealtimeAssetPositions);
        window.markersObj.byLocation[parsedPosition.location][parsedPosition.device_name].setLatLng(L.latLng(parsedPosition.latitude, parsedPosition.longitude));
    }
}

//JavaScript
var stream = {};

if (this.datasource.assetStream.length > 0) {
    stream = JSON.parse(this.datasource.assetStream);
}

if (this.datasource.selectedDevice.name || this.datasource.selectedDevice.name !== "") {
    if (!!stream.name && this.datasource.selectedDevice.name == stream.name) {
        var domElement = document.getElementById("ampsValue")

        if (domElement.innerText) {
            domElement.innerText = stream.amps;
        }
        else if (domElement.textContent) {
            domElement.textContent = stream.amps;   
        }
    }
}

